	member_uid = #{member_uid}

UPDATE mc_member 
SET password = #{pw}
WHERE member_uid = #{member_uid}

mapper.select:
SELECT IFNULL (count(subject) , 0) 
FROM mc_board 
WHERE member_uid = #{member_uid};
